INSERT INTO `jos_vm_product` VALUES (7, 1, 0, 'P01', 'Don''t do it with an axe.  Get a chain saw.', '<ul>  <li>Tool-free tensioner for easy, convenient chain adjustment  </li><li>3-Way Auto Stop; stops chain a fraction of a second  </li><li>Automatic chain oiler regulates oil for proper chain lubrication  </li><li>Small radius guide bar reduces kick-back  </li></ul>  <br />  <strong>Specifications</strong><br />  12.5 AMPS   <br />   16&quot; Bar Length   <br />   3.5 HP   <br />   8.05 LBS. Weight   <br /> ', '8716aefc3b0dce8870360604e6eb8744.jpg', 'c3a5bf074da14f30c849d13a2dd87d2c.jpg', 'Y', 10.0000, 'pounds', 0.0000, 0.0000, 0.0000, 'inches', '', 35, 1088568000, '48h.gif', 'N', 0, NULL, 950321725, 1177350285, 'Chain Saw', 10, '', '', 2, '', 0);
